import React, { useContext } from 'react';
import { Navbar, Container, Nav, Button } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';

import { Context } from '../..';
import { SHOP_ROUTE } from '../../utils/constants';

const NavBar = observer(() => {
  const { user } = useContext(Context);
  return (
    <Navbar bg="dark" variant="dark">
      <Container>
        <Navbar.Brand href={SHOP_ROUTE}>FullStack Shop</Navbar.Brand>
        <Nav className="ml-auto">
          {user.isAuth ? (
            <>
              <Button variant="outline-light m-1">Sign Out</Button>
              <Button variant="outline-light m-1">Admin Page</Button>
            </>
          ) : (
            <>
              <Button variant="outline-light">Authorization</Button>
            </>
          )}
        </Nav>
      </Container>
    </Navbar>
  );
});

export default NavBar;
