import { $authHost, $host } from './index';

export const registration = async ({ email, password }: any) => {
  const response = await $host.post('api/user/registration', {
    email,
    password,
    role: 'ADMIN',
  });
  return response;
};

export const login = async ({ email, password }: any) => {
  const response = await $host.post('api/user/registration', {
    email,
    password,
  });
  return response;
};

