import React from 'react';
import {
  Navbar,
  Container,
  Nav,
  Button,
  Form,
  Card,
  Row,
  Col,
  ListGroup,
  Image,
} from 'react-bootstrap';

const DeviceItem = ({ device }: any) => {
  return (
    <Col md={3}>
      <Card style={{ width: 150, cursor: 'pointer' }} border="light">
        <Image src={device.img} />
      </Card>
    </Col>
  );
};

export default DeviceItem;
