import { makeAutoObservable } from 'mobx';
import isEqual from 'lodash.isequal';

interface IType {
  id: number | null;
  name: string | null;
}

interface ITypes {
  count?: number;
  rows?: IType[];
}

interface IBrand {
  id: number | null;
  name: string | null;
}

interface IBrands {
  count?: number;
  rows?: IBrand[];
}

interface IDevice {
  id: number;
  name: string;
  price: number;
  rating: number;
  img: string;
}

interface IDevices {
  count?: number;
  rows?: IDevice[];
}

export default class DeviceStore {
  _types: ITypes;
  _brands: IBrands;
  _devices: IDevices;
  _selectedType: IType;
  _selectedBrand: IBrand;

  constructor() {
    this._types = {};

    this._brands = {};

    this._devices = {};

    this._selectedType = {
      id: null,
      name: null,
    };

    this._selectedBrand = {
      id: null,
      name: null,
    };

    makeAutoObservable(this);
  }

  setTypes(types: ITypes) {
    if (!this._types.rows) {
      this._types = types;
    }

    if (isEqual(this._types, types)) {
      return;
    }

    this._types = {
      count: types.count,
      rows: [
        ...(this._types.rows as Array<IType>),
        ...(types.rows as Array<IType>),
      ],
    };
  }

  setBrands(brands: IBrands) {
    if (!this._brands.rows) {
      this._brands = brands;
    }

    if (isEqual(this._brands, brands)) {
      return;
    }

    this._types = {
      count: brands.count,
      rows: [
        ...(this._brands.rows as Array<IBrand>),
        ...(brands.rows as Array<IBrand>),
      ],
    };
  }

  setDevices(devices: IDevices) {
    this._devices = devices;
  }

  setSelectedType(type: IType) {
    this._selectedType = type;
  }

  setSelectedBrand(brand: IBrand) {
    this._selectedBrand = brand;
  }

  get types() {
    return this._types;
  }

  get devices() {
    return this._devices;
  }

  get brands() {
    return this._brands;
  }

  get selectedType() {
    return this._selectedType;
  }

  get selectedBrand() {
    return this._selectedBrand;
  }
}
