import React, { useContext } from 'react';
import { Button, Dropdown, Form, Modal } from 'react-bootstrap';
import { Context } from '../../../..';
import ModalWrapper from '../../ModalWrapper/ModalWrapper';

interface IProps {
  show: boolean;
  handleShow: () => void;
}

const CreateDeviceModal = ({ show, handleShow }: IProps) => {
  const { device } = useContext(Context);
  console.log(
    'ðŸš€ ~ file: CreateDeviceModal.tsx ~ line 13 ~ CreateDeviceModal ~ device',
    device,
  );

  return (
    <ModalWrapper show={show} handleShow={handleShow} name="Create Device">
      <Modal.Body>
        <Form>
          <Dropdown>
            <Dropdown.Toggle>Select type</Dropdown.Toggle>
            <Dropdown.Menu>
              {device.types.rows &&
                device.types.rows.map((item: any) => (
                  <Dropdown.Item key={item.id}>{item.name}</Dropdown.Item>
                ))}
            </Dropdown.Menu>
          </Dropdown>
          <Dropdown className="mt-2">
            <Dropdown.Toggle>Select brand</Dropdown.Toggle>
            <Dropdown.Menu>
              {device.brands.rows &&
                device.brands.rows.map((item: any) => (
                  <Dropdown.Item key={item.id}>{item.name}</Dropdown.Item>
                ))}
            </Dropdown.Menu>
          </Dropdown>
          <Form.Control className="mt-3" placeholder="Add Name..." />
          <Form.Control
            className="mt-3"
            placeholder="Add Price..."
            type="number"
          />
          <Form.Control className="mt-3" type="file" />
          <hr />
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleShow}>
          Close
        </Button>
        <Button variant="primary" onClick={handleShow}>
          Save Changes
        </Button>
      </Modal.Footer>
    </ModalWrapper>
  );
};

export default CreateDeviceModal;
