import React, { useContext, useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { Context } from '../../../../..';
import { editBrand } from '../../../../../http/deviceApi';
import ModalWrapper from '../../../ModalWrapper/ModalWrapper';

interface IProps {
  show: boolean;
  handleShow: () => void;
  name: string;
}

const EditBrandOrTypeModal = ({ show, handleShow, name }: IProps) => {
  const [value, setValue] = useState<string>(device.selectedBrand.name);

  const { device } = useContext(Context);

  const patchBrand = (id: string) => {
    editBrand(id, { name: value }).then(() => setValue(''));
    handleShow();
  };
  return (
    <ModalWrapper show={show} handleShow={handleShow} name={`Edit ${name}`}>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Control
              value={value}
              onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
                setValue(event.target.value)
              }
              placeholder="Type new name"
            />
          </Form.Group>
          <div className="d-flex justify-content-between">
            <Button
              onClick={() => patchBrand(device.selectedBrand.id)}
              variant="primary"
              type="submit"
            >
              Submit
            </Button>
            <Button variant="secondary" onClick={handleShow}>
              Close
            </Button>
          </div>
        </Form>
      </Modal.Body>
    </ModalWrapper>
  );
};

export default EditBrandOrTypeModal;
