import React from 'react';
import {
  Navbar,
  Container,
  Nav,
  Button,
  Form,
  Card,
  Row,
} from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { REGISTRATION_ROUTE } from '../utils/constants';

const Auth = () => {
  return (
    <Container
      className="d-flex justify-content-center align-items-center"
      style={{ height: window.innerHeight - 62 }}
    >
      <Card style={{ width: 600 }} className="p-5">
        <h3 className="m-auto">Authorization</h3>
        <Form className="d-flex flex-column">
          <Form.Control className="mt-2" placeholder="Email" />
          <Form.Control
            className="mt-2"
            placeholder="Password"
            type="password"
          />
          <Row className="d-flex justify-space-between mt-3">
            <div>
              Have no account?.. Register{' '}
              <NavLink to={REGISTRATION_ROUTE}>here!</NavLink>
            </div>
            <Button variant="outline-success">Sign In</Button>
          </Row>
        </Form>
      </Card>
    </Container>
  );
};

export default Auth;
