import { observer } from 'mobx-react-lite';
import React, { useContext, useEffect, useState } from 'react';
import { Container, Row, Col, Form } from 'react-bootstrap';

import { Context } from '..';
import AdminBrandBar from '../components/Admin/AdminBrandBar/AdminBrandBar';
import CreateItem from '../components/Admin/CreateItem/CreateItem';
import ItemList from '../components/Admin/ItemList/ItemList';
import BrandBar from '../components/BrandBar/BrandBar';
import TypeBar from '../components/TypeBar/TypeBar';
import CreateItemModal from '../components/Admin/CreateItemModal/CreateItemModal';

import { getTypes, getDevices, getBrands } from '../http/deviceApi';

interface IModalSHow {
  device: boolean;
  type: boolean;
  brand: boolean;
}

const Admin = observer(() => {
  const { device } = useContext(Context);

  const [show, setShow] = useState<IModalSHow>({
    device: false,
    type: false,
    brand: false,
  });

  const handleShow = (modal: string) => setShow<IModalSHow>(!show[modal]);

  useEffect(() => {
    getTypes().then((data) => device.setTypes(data));
    getDevices().then((data) => {
      device.setDevices(data);
    });
    getBrands().then((data) => device.setBrands(data));
  }, []);

  return (
    <Container>
      <Row className="mt-3">
        <Col md={3}>
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Types</Form.Label>
            <Form.Control placeholder="Search..." />
          </Form.Group>
          <TypeBar />
          <div className="mt-5">
            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Brands</Form.Label>
              <Form.Control placeholder="Search..." />
            </Form.Group>
            <AdminBrandBar />
          </div>
        </Col>
        <Col md={9}>
          <CreateItem handleShow={handleShow} />
          <ItemList />
        </Col>
        <CreateItemModal show={show} handleShow={handleShow} />
      </Row>
    </Container>
  );
});

export default Admin;
