import { observer } from 'mobx-react-lite';
import React, { useContext, useState } from 'react';
import { Container, Button, Form, Card } from 'react-bootstrap';
import { NavLink, useLocation } from 'react-router-dom';
import { Context } from '..';
import { login, registration } from '../http/userApi';
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from '../utils/constants';

const Auth = observer(() => {
  const location = useLocation();
  const isLogin = location.pathname === LOGIN_ROUTE;

  const { user } = useContext(Context);

  const [inputs, setInputs] = useState({
    email: '',
    password: '',
  });

  console.log(inputs, 'inputs');

  const handleInputsChange = (event: React.FormEvent<HTMLInputElement>) => {
    setInputs((prev) => ({
      ...prev,
      [(event.target as HTMLInputElement).name]: (
        event.target as HTMLInputElement
      ).value,
    }));
  };

  const signIn = async () => {
    try {
      let data: any;
      if (isLogin) {
        data = await login(inputs);
      } else {
        const data = await registration(inputs);
        console.log('response', data);
      }
      user.setUser(user);
      user.setIsAuth(true);
    } catch (error) {
      alert(e.response.data.message);
    }
  };

  return (
    <Container
      className="d-flex justify-content-center align-items-center"
      style={{ height: window.innerHeight - 62 }}
    >
      <Card style={{ width: 600 }} className="p-5">
        <h3 className="m-auto">{isLogin ? 'Authorization' : 'Registration'}</h3>
        <Form className="d-flex flex-column">
          <input
            name="email"
            className="mt-2"
            placeholder="Email"
            type="email"
            value={inputs.email}
            onChange={(e) => handleInputsChange(e)}
          />
          <input
            name="password"
            className="mt-2"
            placeholder="Password"
            type="password"
            value={inputs.password}
            onChange={(e) => handleInputsChange(e)}
          />
          {isLogin ? (
            <>
              <div className="mt-3">
                Have no account?{' '}
                <NavLink to={REGISTRATION_ROUTE}>Register!</NavLink>
              </div>
              <Button className="mt-2" variant="outline-success">
                Sign In
              </Button>
            </>
          ) : (
            <>
              <div className="mt-3">
                Have account? <NavLink to={LOGIN_ROUTE}>Sign In!</NavLink>
              </div>
              <Button
                onClick={signIn}
                className="mt-2"
                variant="outline-success"
              >
                Register
              </Button>
            </>
          )}
        </Form>
      </Card>
    </Container>
  );
});

export default Auth;
