import React, { useState } from 'react';
import {
  Navbar,
  Container,
  Nav,
  Button,
  Form,
  Card,
  Row,
} from 'react-bootstrap';
import { NavLink, useLocation } from 'react-router-dom';
import { login, registration } from '../http/userApi';
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from '../utils/constants';

const Auth = () => {
  const location = useLocation();
  const isLogin = location.pathname === LOGIN_ROUTE;

  const [inputs, setInputs] = useState({
    email: '',
    password: '',
  });

  const signIn = async () => {
    if (isLogin) {
      const response = await login();
    } else {
      const response = await registration();
    }
  };

  return (
    <Container
      className="d-flex justify-content-center align-items-center"
      style={{ height: window.innerHeight - 62 }}
    >
      <Card style={{ width: 600 }} className="p-5">
        <h3 className="m-auto">{isLogin ? 'Authorization' : 'Registration'}</h3>
        <Form className="d-flex flex-column">
          <Form.Control
            className="mt-2"
            placeholder="Email"
            type="email"
            value={inputs.email}
          />
          <Form.Control
            className="mt-2"
            placeholder="Password"
            type="password"
            value={inputs.password}
          />
          {isLogin ? (
            <>
              <div className="mt-3">
                Have no account?{' '}
                <NavLink to={REGISTRATION_ROUTE}>Register!</NavLink>
              </div>
              <Button className="mt-2" variant="outline-success">
                Sign In
              </Button>
            </>
          ) : (
            <>
              <div className="mt-3">
                Have account? <NavLink to={LOGIN_ROUTE}>Sign In!</NavLink>
              </div>
              <Button className="mt-2" variant="outline-success">
                Register
              </Button>
            </>
          )}
        </Form>
      </Card>
    </Container>
  );
};

export default Auth;
