import { observer } from 'mobx-react-lite';
import React, { useContext } from 'react';
import { ListGroup, Card, Button } from 'react-bootstrap';

import { Context } from '../../..';

import Delete from '../../../assets/icons/Delete';
import Edit from '../../../assets/icons/Edit';

import { deleteType } from '../../../http/deviceApi';

interface IProps {
  handleShow: () => void;
}

const AdminTypeBar = observer(({ handleShow }: IProps) => {
  const { device } = useContext(Context);

  return (
    <ListGroup horizontal className="d-flex flex-column gap-2">
      {device.types.rows &&
        device.types.rows.map((type: any) => (
          <ListGroup.Item
            style={{ cursor: 'pointer' }}
            onClick={() => device.setSelectedType(type)}
            border={type.id === device.selectedType.id ? 'primary' : ''}
            key={type.id}
            className="p-2 d-flex justify-content-between"
          >
            {type.name}
            {/* <Card.Footer className="d-flex mt-1 justify-content-between"> */}
            <Button onClick={() => deleteType(type.id)} size="sm">
              <Delete />
            </Button>
            <Button onClick={handleShow} size="sm">
              <Edit />
            </Button>
            {/* </Card.Footer> */}
          </ListGroup.Item>
        ))}
    </ListGroup>
  );
});

export default AdminTypeBar;
