import React from 'react';
import { Card, Col, Image, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

import { DEVICE_ROUTE } from '../../utils/constants';

const DeviceItem = ({ device }: any) => {
  const navigate = useNavigate();

  return (
    // <Col md={3} onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}>
    //   <Card style={{ width: 150, cursor: 'pointer' }} border="light">
    //     <Image
    //       width={150}
    //       height={150}
    //       src={process.env.REACT_APP_HOST_URL + device.img}
    //     />
    //     <div className="d-flex justify-content-space-between align-items-center flex-column mt-2">
    //       <div>{device.name}</div>
    //       <div>rating {device.rating}</div>
    //     </div>
    //   </Card>
    // </Col>
    <Col md={6}>
      <Card
        style={{ width: '18rem', cursor: 'pointer', padding: '0' }}
        onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}
        border="light"
        bg="light"
      >
        <Card.Img
          variant="top"
          src={process.env.REACT_APP_HOST_URL + device.img}
          style={{ width: '100%', height: '250px', objectFit: 'cover' }}
        />
        <Card.Body>
          <Card.Title>{device.name}</Card.Title>
          <Card.Text>Users Rating {device.rating}</Card.Text>
        </Card.Body>
      </Card>
    </Col>
  );
};

export default DeviceItem;
