import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Button, Card, ListGroup } from 'react-bootstrap';

import { Context } from '../../..';
import { deleteBrand } from '../../../http/deviceApi';
import Delete from '../../../assets/icons/Delete';
import Edit from '../../../assets/icons/Edit';

const AdminBrandBar = observer(() => {
  const { device } = useContext(Context);

  return (
    <ListGroup horizontal className="d-flex flex-column gap-2">
      {device.brands.rows &&
        device.brands.rows.map((brand: any) => (
          <Card
            style={{ cursor: 'pointer' }}
            onClick={() => device.setSelectedBrand(brand)}
            border={brand.id === device.selectedBrand.id ? 'primary' : ''}
            key={brand.id}
            className="p-2 d-flex justify-content-between"
          >
            {brand.name}
            <Card.Footer className="d-flex mt-1 justify-content-between">
              {/* <Button onClick={() => deleteBrand(brand.id)} size="sm"> */}
              <Delete />
              {/* </Button> */}
              {/* <Button size="sm"> */}
              <Edit />
              {/* </Button> */}
            </Card.Footer>
          </Card>
        ))}
    </ListGroup>
  );
});

export default AdminBrandBar;
