import { observer } from 'mobx-react-lite';
import React, { useContext } from 'react';
import { ListGroup, Card, Button, Form } from 'react-bootstrap';

import { Context } from '../../..';

import Delete from '../../../assets/icons/Delete';
import Edit from '../../../assets/icons/Edit';

import { deleteType } from '../../../http/deviceApi';

interface IProps {
  handleShow: () => void;
}

const AdminTypeBar = observer(({ handleShow }: IProps) => {
  const { device } = useContext(Context);

  return (
    <Form>
      <ListGroup as="ul">
        {device.types.rows &&
          device.types.rows.map((type: any) => (
            <ListGroup.Item
              style={{ cursor: 'pointer' }}
              onClick={() => device.setSelectedType(type)}
              active={type.id === device.selectedType.id}
              key={type.id}
              className="p-2 d-flex justify-content-between"
            >
              <Form.Check
                inline
                // label="1"
                // name="group1"
                type="checkbox"
              />
              {type.name}
              <div className="d-flex mt-1 justify-content-between gap-2">
                <Button
                  variant="danger"
                  onClick={() => deleteType(type.id)}
                  size="sm"
                >
                  <Delete />
                </Button>
                <Button variant="warning" onClick={handleShow} size="sm">
                  <Edit />
                </Button>
              </div>
            </ListGroup.Item>
          ))}
      </ListGroup>
    </Form>
  );
});

export default AdminTypeBar;
