import React, { useContext, useRef, useState } from 'react';
import { observer } from 'mobx-react-lite';
import {
  Button,
  Card,
  Form,
  ListGroup,
  Overlay,
  Tooltip,
} from 'react-bootstrap';

import { Context } from '../../..';
import { deleteBrand } from '../../../http/deviceApi';
import Delete from '../../../assets/icons/Delete';
import Edit from '../../../assets/icons/Edit';

interface IProps {
  handleShow: () => void;
}

const AdminBrandBar = observer(({ handleShow }: IProps) => {
  const { device } = useContext(Context);

  const [show, setShow] = useState(false);
  const target = useRef(null);
  console.log(
    'ðŸš€ ~ file: AdminBrandBar.tsx ~ line 26 ~ AdminBrandBar ~ target',
    target,
  );

  const [allCount, setAllCount] = useState(3);

  //TODO: ListItem component

  return (
    <>
      <ListGroup>
        {device.brands.rows &&
          device.brands.rows.slice(0, allCount).map((brand: any) => (
            <ListGroup.Item
              style={{ cursor: 'pointer' }}
              onClick={() => device.setSelectedBrand(brand)}
              // active={brand.id === device.selectedBrand.id}
              key={brand.id}
              className="p-2 d-flex justify-content-between"
            >
              <div>
                <input
                  key={brand.id + 'tooltip'}
                  ref={target}
                  className="m-0 p-0"
                  onChange={() => setShow(!show)}
                  type="checkbox"
                />
                <Overlay target={target.current} show={show} placement="left">
                  {(props) => <Tooltip {...props}>My Tooltip</Tooltip>}
                </Overlay>
              </div>

              {brand.name}
              <div className="d-flex mt-1 justify-content-between gap-2">
                <Button
                  variant="danger"
                  onClick={() => deleteBrand(brand.id)}
                  size="sm"
                >
                  <Delete />
                </Button>
                <Button variant="warning" onClick={handleShow} size="sm">
                  <Edit />
                </Button>
              </div>
            </ListGroup.Item>
          ))}
      </ListGroup>
      <Button
        className="mt-3 mb-3 d-flex w-100 justify-content-center"
        variant="outline-primary"
        size="sm"
        onClick={() => setAllCount(device.brands.count)}
      >
        Show all
      </Button>
    </>
  );
});

export default AdminBrandBar;
