import React, { useContext, useEffect, useState } from 'react';
import { nanoid } from 'nanoid';
import { Button, Col, Dropdown, Form, Modal, Row } from 'react-bootstrap';
import { Context } from '../../../..';
import { getTypes, getBrands, createDevice } from '../../../../http/deviceApi';
import ModalWrapper from '../../ModalWrapper/ModalWrapper';

interface IProps {
  show: boolean;
  handleShow: () => void;
}

interface IDeviceState {
  name: string;
  price: number;
  file: string | Blob;
  brandId: string;
  typeId: string;
  // info: IDeviceInfo[];
}

interface IDeviceInfo {
  title: string;
  description: string;
  id: string;
}

const CreateDeviceModal = ({ show, handleShow }: IProps) => {
  const { device } = useContext(Context);

  const [deviceState, setDeviceState] = useState<IDeviceState>({
    name: '',
    price: 0,
    file: '',
    brandId: '',
    typeId: '',
    // info: [],
  });

  useEffect(() => {
    getTypes().then((data) => device.setTypes(data));
    getBrands().then((data) => device.setBrands(data));
  }, []);

  const addInfo = () => {
    setDeviceState((prevValue: any) => ({
      ...prevValue,
      info: [...prevValue.info, { title: '', description: '', id: nanoid() }],
    }));
  };
  // const removeInfo = (id: any) => {
  //   setDeviceState((prevValue: any) => ({
  //     ...prevValue,
  //     info: deviceState.info.filter((i: any) => i.id !== id),
  //   }));
  // };

  // const changeInfo = (key: any, value: any, id: any) => {
  //   setDeviceState((prevValue: any) => ({
  //     ...prevValue,
  //     info: deviceState.info.map((i: any) =>
  //       i.id === id ? { ...i, [key]: value } : i,
  //     ),
  //   }));
  // };

  const selectFile = (e: any) => {
    setDeviceState((prevValue: any) => ({
      ...prevValue,
      file: e.target.files[0],
    }));
  };

  const addDevice = () => {
    const formData = new FormData();
    formData.append('name', deviceState.name);
    formData.append('price', `${deviceState.price}`);
    formData.append('img', deviceState.file);
    formData.append('brandId', device.selectedBrand.id);
    formData.append('typeId', device.selectedType.id);
    // formData.append('info', JSON.stringify(deviceState.info));
    createDevice(formData).then(() => handleShow());
  };

  return (
    <ModalWrapper show={show} handleShow={handleShow} name="Create Device">
      <Modal.Body>
        <Form>
          <Dropdown>
            <Dropdown.Toggle>
              {device.selectedType.name || 'Select type'}
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {device.types.rows &&
                device.types.rows.map((type: any) => (
                  <Dropdown.Item
                    onClick={() =>
                      setDeviceState((prevValue: any) => ({
                        ...prevValue,
                        typeId: type.id,
                      }))
                    }
                    key={type.id}
                  >
                    {type.name}
                  </Dropdown.Item>
                ))}
            </Dropdown.Menu>
          </Dropdown>
          <Dropdown className="mt-2">
            <Dropdown.Toggle>
              {device.selectedBrand.name || 'Select brand'}
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {device.brands.rows &&
                device.brands.rows.map((brand: any) => (
                  <Dropdown.Item
                    onClick={() =>
                      setDeviceState((prevValue: any) => ({
                        ...prevValue,
                        brandId: brand.id,
                      }))
                    }
                    key={brand.id}
                  >
                    {brand.name}
                  </Dropdown.Item>
                ))}
            </Dropdown.Menu>
          </Dropdown>
          <Form.Control
            onChange={(e) =>
              setDeviceState((prevValue: any) => ({
                ...prevValue,
                name: e.target.value,
              }))
            }
            className="mt-3"
            placeholder="Add Name..."
          />
          <Form.Control
            onChange={(e) =>
              setDeviceState((prevValue: any) => ({
                ...prevValue,
                price: e.target.value,
              }))
            }
            className="mt-3"
            placeholder="Add Price..."
            type="number"
          />
          <Form.Control className="mt-3" type="file" onChange={selectFile} />
          <hr />
          <Button variant={'outline-dark'} onClick={addInfo}>
            Add new property
          </Button>
          {/* {deviceState.info &&
            deviceState.info.map((i) => (
              <Row className="mt-4" key={i.id}>
                <Col md={4}>
                  <Form.Control
                    value={i.title}
                    onChange={(e) => changeInfo('title', e.target.value, i.id)}
                    placeholder="Add property name"
                  />
                </Col>
                <Col md={4}>
                  <Form.Control
                    value={i.description}
                    onChange={(e) =>
                      changeInfo('description', e.target.value, i.id)
                    }
                    placeholder="Add property description"
                  />
                </Col>
                <Col md={4}>
                  <Button
                    onClick={() => removeInfo(i.id)}
                    variant={'outline-danger'}
                  >
                    Delete
                  </Button>
                </Col>
              </Row>
            ))} */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleShow}>
          Close
        </Button>
        <Button variant="outline-success" onClick={addDevice}>
          Save Changes
        </Button>
      </Modal.Footer>
    </ModalWrapper>
  );
};

export default CreateDeviceModal;
