import { makeAutoObservable } from 'mobx';

interface IType {
  id: number | null;
  name: string | null;
}

interface IBrand {
  id: number;
  name: string;
}

interface IDevice {
  id: number;
  name: string;
  price: number;
  rating: number;
  img: string;
}

export default class DeviceStore {
  _types: IType[] = [];
  _brands: IBrand[] = [];
  _devices: IDevice[] = [];
  _selectedType: IType = {
    id: null,
    name: null,
  };

  constructor() {
    this._types = [
      { id: 1, name: 'Phones' },
      { id: 2, name: 'Laptops' },
      { id: 3, name: 'Fridges' },
      { id: 4, name: 'Monitors' },
    ];

    this._brands = [
      { id: 1, name: 'Apple' },
      { id: 2, name: 'Sony' },
    ];

    this._devices = [
      {
        id: 1,
        name: 'Iphone 1',
        price: 1000,
        rating: 5,
        img: 'https://gizmobo.com/wp-content/uploads/2021/12/Apple-iPhone-1st-gen-1.webp',
      },
      {
        id: 2,
        name: 'Iphone 3g',
        price: 1500,
        rating: 5,
        img: 'https://hotline.ua/img/tx/069/0698895.jpg',
      },
      {
        id: 3,
        name: 'Iphone 4',
        price: 2000,
        rating: 5,
        img: 'https://hotline.ua/img/tx/735/7351675.jpg',
      },
      {
        id: 4,
        name: 'Iphone 5',
        price: 2500,
        rating: 5,
        img: 'https://hotline.ua/img/tx/158/1581420625.jpg',
      },
    ];

    this._selectedType = {
      id: null,
      name: null,
    };

    makeAutoObservable(this);
  }

  setTypes(types: IType[]) {
    this._types = types;
  }

  setBrands(brands: IBrand[]) {
    this._brands = brands;
  }

  setDevices(devices: IDevice[]) {
    this._devices = devices;
  }

  setSelectedType(type: IType) {
    this._selectedType = type;
  }

  get types() {
    return this._types;
  }

  get devices() {
    return this._devices;
  }

  get brands() {
    return this._brands;
  }

  get selectedType() {
    return this._selectedType;
  }
}
